func addGrouped(group: var GroupedArticles; name: string; article: NcbArticle) =
  var groupName = name.strip
  if not group.hasKey groupName:
    group[groupName] = @[]
  group[groupName].add article

proc getNbcHome*: Future[NbcHome] {.async.} =
  ## Extracts the main page of NBC NEWS
  new result
  let
    client = newAsyncHttpClient(headers = newHttpHeaders({
      "user-agent": mozilla
    }))
    # html = parseHtml await client.getContent url
    data = jsonData.parseJson # html.getNextData

  # var allArticles: Table[string, NcbArticle]
  for layout in data{"props", "initialState", "front", "curation", "layouts"}:
    for package in layout{"packages"}:
      for item in package{"items"}:
        if item{"type"}.getStr != "article": continue
        let
          article = new NcbArticle
          computedValues = item{"computedValues"}
          item = item{"item"}
        article.title = computedValues{"headline"}.getStr
        article.breakingNews = item{"breakingNews"}.getBool
        
        block image:
          let teaseImage = computedValues{"teaseImage"}
          if teaseImage.kind == JNull: break image
          article.image = new NcbArticleImage
          article.image.src = teaseImage{"url", "primary"}.getStr
          article.image.publisher = teaseImage{"publisher", "name"}.getStr
          article.image.source = teaseImage{"source", "name"}.getStr
          article.image.alt = teaseImage{"altText"}.getStr
          article.image.caption = teaseImage{"caption"}.getStr
          for author in teaseImage{"authors"}:
            article.image.authors.add author{"name"}.getStr


        # saving
        case package{"type"}.getStr:
        of "threeUp", "oneUp": result.highlighted.add article
        of "aLaCarte": result.byGroup.addGrouped(package{"name"}.getStr,  article)
        of "pancake": result.byCategory.addGrouped(package{"name"}.getStr, article)

    # allArticles[article.title] = article
  # for a in allArticles.keys:
  #   echo a
  close client

when isMainModule:
  import std/jsonutils
  echo (waitFor getNbcHome())[].toJson.pretty
