proc getNbcHome*(url = "https://www.nbcnews.com/"): Future[NbcHome] {.async.} =
  ## Extracts the main page of NBC NEWS
  ## 
  ## Works with:
  ## - https://www.nbcnews.com
  ## - https://www.today.com
  ## - https://www.today.com/news
  ## 
  ## TODO:
  ## - https://www.nbcnews.com/latest-stories/
  ## - https://www.nbcnews.com/category
  ## - https://www.nbcnews.com/video
  new result
  let
    client = newAsyncHttpClient(headers = newHttpHeaders({
      "user-agent": mozilla
    }))
    html = parseHtml await client.getContent url
    data = html.getNextData
    # data = jsonData.parseJson

  for layout in data{"props", "initialState", "front", "curation", "layouts"}:
    for package in layout{"packages"}:
      for it in package{"items"}:
        let itemType = it{"type"}.getStr
        case itemType:
        of "article":
          var article = extractArticle it
          for related in it{"related"}:
            let relatedArticle = extractArticle related
            article.related.add relatedArticle

          # saving
          case package{"type"}.getStr:
          of "threeUp", "oneUp", "leadSectionFront": result.highlighted.add article
          of "aLaCarte": result.byGroup.addGrouped(package{"name"}.getStr,  article)
          of "pancake": result.byCategory.addGrouped(package{"name"}.getStr, article)
          of "teaseList": result.teases.addGrouped(package{"metadata", "title"}.getStr, article)
        of "video":
          var video = extractVideo it
          result.videos.add video
        else: discard

  close client

when isMainModule:
  import std/jsonutils
  echo (waitFor getNbcHome("https://www.nbcnews.com/video")).videos.toJson.pretty
